mapper.insert:
	insert into fb_contract_over (fco_id,prj_code, cj_contract, cj_name,fb_money,fb_end_money,
    fb_contract,fb_name,settlement_time, work_info
    )
  values (#{fb.fcoId},#{fb.prjCode},#{fb.cjContract}, #{fb.cjName},#{fb.fbMoney},#{fb.fbEndMoney},
    #{fb.fbContract}, #{fb.fbName},#{fb.settlementType}, #{fb.settlementTime}, #{fb.workInfo}
    )

mapper.select:
select a.* from fb_contract_over as a 
left join mode_status as b on a.fco_id=b.mode_id
where b.status=#{state}
