mapper.select:
SELECT a.*, c.BusinessName, b.`flow_status` FROM flowhistroy a LEFT JOIN mode_status b ON a.mode_id = b.mode_id LEFT JOIN business c ON a.Bus_ID = c.ID where a.Actor=#{userId}
and b.`flow_status`=#{status}
and IFNULL(a.delete,'1') !=0 order by a.do_date desc

SELECT a.*, c.BusinessName, b.`flow_status`,count(DISTINCT a.URL) FROM flowhistroy a LEFT JOIN mode_status b ON a.mode_id = b.mode_id LEFT JOIN business c ON a.Bus_ID = c.ID where a.Actor=#{userId}
and b.`flow_status`=#{status}
and IFNULL(a.delete,'1') !=0 GROUP BY a.URL order by a.do_date desc
