mapper.select:
		SELECT
			a.*,
			c.BusinessName,
			b.`status`,count(DISTINCT a.URL)
		FROM
			flowhistroy a
		LEFT JOIN mode_status b ON a.mode_id = b.mode_id
		LEFT JOIN business c ON a.Bus_ID = c.ID where a.Actor=#{userId}
		
        and b.`status`=#{status}
       	
		  GROUP BY a.URL ;
