mapper.select:
SELECT * FROM gs_cl_out where gs_out_id in(
	SELECT
		cmo_id
	FROM
		company_material_out
	WHERE
		cg_no = #{cgNo}
	AND cg_name = #{cgName}
	AND task_no = #{taskNo}
	AND get_status = 1
	AND remain_type = 0
)

SELECT * FROM gs_cl_out where gs_out_id in(
	SELECT
		cmo_id
	FROM
		company_material_out
	WHERE
		cg_no = #{cgNo}
	AND cg_name = #{cgName}
	AND pick_project = #{name}
	AND get_status = 1
	AND remain_type = 0
)
