mapper.select:
select * from company_material_in a
left join mode_status m on m.mode_id=a.cm_id
where m.status=2 and a.out_state=#{outState}

mapper.update:
update company_material_in 
set 
	get_person=#{gsIncl.getPerson},
	get_infos=#{gsIncl.getInfos},
	get_status=#{gsIncl.getStatus}
where cm_id=#{gsIncl.cmId}
